mapper.select:
SELECT *
FROM board
WHERE category = 'free'
ORDER BY num DESC
    LIMIT #{pageSize}
OFFSET #{offset}

SELECT *
FROM board
WHERE category = 'qna'
ORDER BY num DESC
    LIMIT #{pageSize}
OFFSET #{offset}

mapper.update:
UPDATE board
SET boardhitcount = boardhitcount + 1
WHERE num = #{num};
