mapper.select:
SELECT *
FROM work
WHERE item_id = #{item_id}
  AND type = 'webtoon';

SELECT * FROM work WHERE type = 'webtoon' AND (
categories LIKE CONCAT('%', #{category}, '%')
) ORDER BY item_id DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT * FROM work WHERE type = 'webtoon' AND (
categories LIKE CONCAT('%', #{category}, '%')
) ORDER BY item_id DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM work WHERE type = 'webtoon' AND (
categories LIKE CONCAT('%', #{category}, '%')
)

SELECT COUNT(*) FROM work WHERE type = 'webtoon' AND (
categories LIKE CONCAT('%', #{category}, '%')
)

mapper.update:
UPDATE work
SET update_day = #{update_day},
    link = #{link}
WHERE title = #{title}
  AND type = 'webtoon'

UPDATE Webtoon
SET added_bs = added_bs + 1
WHERE item_id = #{workNum}
